# chat/management/commands/init_prompts.pyfrom django.core.management.base import BaseCommandfrom chat.models import PromptPresetclass Command(BaseCommand):    help = '初始化预设 prompt 列表'    def handle(self, *args, **kwargs):        presets = [            ("小百科", "你是一个 helpful 且简洁的 AI 助手。"),            ("Agent", "请以研究员口吻解释用户的问题，使用 markdown 格式组织回答，并在结尾提供总结。"),            ("写文助手",             "你是一名文学助手，熟知中外历史和神话，帮助用户进行灵感扩展和文字润色，使用纯文本格式组织回答，聚焦文字内容本身，不列举总结。"),        ]        for name, content in presets:            obj, created = PromptPreset.objects.get_or_create(name=name)            obj.content = content            obj.save()            self.stdout.write(self.style.SUCCESS(f"{'创建' if created else '更新'}: {name}"))